import React, { useState, useRef, useEffect } from 'react';
import { Camera, AlertCircle, Sparkles, Users, Smile } from 'lucide-react';

export default function EmotionDetectionApp() {
  const [isStreaming, setIsStreaming] = useState(false);
  const [emotionData, setEmotionData] = useState(null);
  const [error, setError] = useState(null);
  const [isAnalyzing, setIsAnalyzing] = useState(false);
  const [apiStatus, setApiStatus] = useState('checking');
  
  const videoRef = useRef(null);
  const canvasRef = useRef(null);
  const streamRef = useRef(null);
  const intervalRef = useRef(null);

  // Emotion colors
  const emotionColors = {
    happy: '#10B981',
    sad: '#3B82F6',
    angry: '#EF4444',
    surprise: '#F59E0B',
    fear: '#8B5CF6',
    disgust: '#06B6D4',
    neutral: '#9CA3AF'
  };

  // Check API health on mount
  useEffect(() => {
    checkApiHealth();
    return () => {
      stopWebcam();
    };
  }, []);

  const checkApiHealth = async () => {
    try {
      const response = await fetch('http://localhost:5000/api/health');
      if (response.ok) {
        setApiStatus('connected');
      } else {
        setApiStatus('error');
      }
    } catch (err) {
      setApiStatus('error');
      setError('Cannot connect to backend API. Make sure Flask server is running on port 5000.');
    }
  };

  const startWebcam = async () => {
    try {
      const stream = await navigator.mediaDevices.getUserMedia({ 
        video: { width: 640, height: 480 } 
      });
      
      if (videoRef.current) {
        videoRef.current.srcObject = stream;
        streamRef.current = stream;
        setIsStreaming(true);
        setError(null);
        
        // Start analyzing frames
        intervalRef.current = setInterval(captureAndAnalyze, 1000);
      }
    } catch (err) {
      setError('Failed to access webcam. Please grant camera permissions.');
      console.error('Webcam error:', err);
    }
  };

  const stopWebcam = () => {
    if (streamRef.current) {
      streamRef.current.getTracks().forEach(track => track.stop());
      streamRef.current = null;
    }
    
    if (intervalRef.current) {
      clearInterval(intervalRef.current);
      intervalRef.current = null;
    }
    
    setIsStreaming(false);
    setEmotionData(null);
  };

  const captureAndAnalyze = async () => {
    if (!videoRef.current || !canvasRef.current || isAnalyzing) return;
    
    setIsAnalyzing(true);
    
    const video = videoRef.current;
    const canvas = canvasRef.current;
    const context = canvas.getContext('2d');
    
    canvas.width = video.videoWidth;
    canvas.height = video.videoHeight;
    context.drawImage(video, 0, 0);
    
    const imageData = canvas.toDataURL('image/jpeg', 0.8);
    
    try {
      const response = await fetch('http://localhost:5000/api/analyze-emotion', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({ image: imageData }),
      });
      
      const data = await response.json();
      
      if (data.success) {
        setEmotionData(data);
        setError(null);
      } else {
        setError(data.error || 'Failed to analyze emotion');
      }
    } catch (err) {
      setError('Failed to connect to API');
      console.error('API error:', err);
    } finally {
      setIsAnalyzing(false);
    }
  };

  const dominantColor = emotionData?.dominant_emotion 
    ? emotionColors[emotionData.dominant_emotion.toLowerCase()] || '#9CA3AF'
    : '#9CA3AF';

  return (
    <div className="min-h-screen bg-gradient-to-br from-purple-900 via-blue-900 to-indigo-900 text-white p-8">
      <div className="max-w-6xl mx-auto">
        {/* Header */}
        <div className="text-center mb-8">
          <div className="flex items-center justify-center gap-3 mb-4">
            <Sparkles className="w-8 h-8 text-yellow-400" />
            <h1 className="text-4xl font-bold">Emotion Detection System</h1>
            <Sparkles className="w-8 h-8 text-yellow-400" />
          </div>
          <p className="text-gray-300">Real-time facial emotion recognition using AI</p>
        </div>

        {/* API Status */}
        <div className="mb-6 flex items-center justify-center gap-2">
          <div className={`w-3 h-3 rounded-full ${
            apiStatus === 'connected' ? 'bg-green-500' : 
            apiStatus === 'error' ? 'bg-red-500' : 
            'bg-yellow-500'
          } animate-pulse`}></div>
          <span className="text-sm">
            {apiStatus === 'connected' ? 'Connected to API' : 
             apiStatus === 'error' ? 'API Disconnected' : 
             'Checking API...'}
          </span>
        </div>

        {/* Main Content Grid */}
        <div className="grid grid-cols-1 lg:grid-cols-3 gap-6">
          {/* Video Section */}
          <div className="lg:col-span-2">
            <div className="bg-black/40 backdrop-blur-sm rounded-2xl p-6 border border-white/10">
              <div className="relative aspect-video bg-gray-900 rounded-lg overflow-hidden mb-4">
                {!isStreaming ? (
                  <div className="absolute inset-0 flex flex-col items-center justify-center">
                    <Camera className="w-20 h-20 text-gray-500 mb-4" />
                    <p className="text-gray-400">Click Start to begin</p>
                  </div>
                ) : (
                  <>
                    <video
                      ref={videoRef}
                      autoPlay
                      playsInline
                      className="w-full h-full object-cover"
                    />
                    {emotionData && emotionData.faces.map((face, idx) => (
                      <div
                        key={idx}
                        className="absolute border-4 rounded"
                        style={{
                          left: `${(face.x / 640) * 100}%`,
                          top: `${(face.y / 480) * 100}%`,
                          width: `${(face.width / 640) * 100}%`,
                          height: `${(face.height / 480) * 100}%`,
                          borderColor: dominantColor,
                        }}
                      />
                    ))}
                  </>
                )}
              </div>
              
              <canvas ref={canvasRef} className="hidden" />
              
              <div className="flex gap-3">
                {!isStreaming ? (
                  <button
                    onClick={startWebcam}
                    disabled={apiStatus === 'error'}
                    className="flex-1 bg-gradient-to-r from-green-500 to-emerald-600 hover:from-green-600 hover:to-emerald-700 disabled:from-gray-600 disabled:to-gray-700 px-6 py-3 rounded-lg font-semibold flex items-center justify-center gap-2 transition-all"
                  >
                    <Camera className="w-5 h-5" />
                    Start Webcam
                  </button>
                ) : (
                  <button
                    onClick={stopWebcam}
                    className="flex-1 bg-gradient-to-r from-red-500 to-rose-600 hover:from-red-600 hover:to-rose-700 px-6 py-3 rounded-lg font-semibold transition-all"
                  >
                    Stop Webcam
                  </button>
                )}
              </div>

              {error && (
                <div className="mt-4 bg-red-500/20 border border-red-500 rounded-lg p-4 flex items-start gap-3">
                  <AlertCircle className="w-5 h-5 text-red-400 flex-shrink-0 mt-0.5" />
                  <p className="text-sm text-red-200">{error}</p>
                </div>
              )}
            </div>
          </div>

          {/* Results Section */}
          <div className="space-y-6">
            {/* Dominant Emotion Card */}
            <div className="bg-black/40 backdrop-blur-sm rounded-2xl p-6 border border-white/10">
              <h3 className="text-xl font-semibold mb-4 flex items-center gap-2">
                <Smile className="w-6 h-6" />
                Current Emotion
              </h3>
              
              {emotionData ? (
                <div className="text-center">
                  <div 
                    className="text-5xl font-bold mb-2"
                    style={{ color: dominantColor }}
                  >
                    {emotionData.dominant_emotion.toUpperCase()}
                  </div>
                  <div className="flex items-center justify-center gap-2 text-gray-400">
                    <Users className="w-4 h-4" />
                    <span>{emotionData.face_count} face(s) detected</span>
                  </div>
                </div>
              ) : (
                <div className="text-center text-gray-400 py-8">
                  <Smile className="w-12 h-12 mx-auto mb-2 opacity-50" />
                  <p>Waiting for detection...</p>
                </div>
              )}
            </div>

            {/* All Emotions Card */}
            <div className="bg-black/40 backdrop-blur-sm rounded-2xl p-6 border border-white/10">
              <h3 className="text-xl font-semibold mb-4">Emotion Scores</h3>
              
              {emotionData && emotionData.emotions ? (
                <div className="space-y-3">
                  {Object.entries(emotionData.emotions)
                    .sort(([, a], [, b]) => b - a)
                    .map(([emotion, score]) => (
                      <div key={emotion}>
                        <div className="flex justify-between text-sm mb-1">
                          <span className="capitalize">{emotion}</span>
                          <span className="font-semibold">{score.toFixed(1)}%</span>
                        </div>
                        <div className="w-full bg-gray-700 rounded-full h-2">
                          <div
                            className="h-2 rounded-full transition-all duration-500"
                            style={{
                              width: `${score}%`,
                              backgroundColor: emotionColors[emotion] || '#9CA3AF'
                            }}
                          />
                        </div>
                      </div>
                    ))}
                </div>
              ) : (
                <div className="text-center text-gray-400 py-8">
                  <div className="w-12 h-12 mx-auto mb-2 border-4 border-gray-600 border-t-gray-400 rounded-full animate-spin"></div>
                  <p className="text-sm">Start webcam to see scores</p>
                </div>
              )}
            </div>
          </div>
        </div>

        {/* Instructions */}
        <div className="mt-8 bg-black/20 backdrop-blur-sm rounded-2xl p-6 border border-white/10">
          <h3 className="font-semibold mb-3">Instructions:</h3>
          <ul className="list-disc list-inside space-y-2 text-gray-300 text-sm">
            <li>Make sure the Flask backend is running on http://localhost:5000</li>
            <li>Click "Start Webcam" to begin emotion detection</li>
            <li>Face the camera directly for best results</li>
            <li>Emotions are analyzed every second</li>
            <li>Multiple faces can be detected simultaneously</li>
          </ul>
        </div>
      </div>
    </div>
  );
}
